import { useEffect, useRef } from 'react'

import { FieldError, Form, Label, TextField } from '@cedarjs/forms'
import { navigate, routes } from '@cedarjs/router'
import { Metadata } from '@cedarjs/web'
import { toast, Toaster } from '@cedarjs/web/toast'

import { useAuth } from 'src/auth'
import { Button } from '@/registry/default/ui/Button'
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/registry/default/ui/Card'

const ForgotPasswordPage = () => {
  const { isAuthenticated, forgotPassword } = useAuth()

  useEffect(() => {
    if (isAuthenticated) {
      navigate(routes.home())
    }
  }, [isAuthenticated])

  const ${usernameCamelCase}Ref = useRef<HTMLInputElement>(null)
  useEffect(() => {
    ${usernameCamelCase}Ref?.current?.focus()
  }, [])

  const onSubmit = async (data: { ${usernameCamelCase}: string }) => {
    const response = await forgotPassword(data.${usernameCamelCase})

    if (response.error) {
      toast.error(response.error)
    } else {
      // The function `forgotPassword.handler` in api/src/functions/auth.js has
      // been invoked, let the user know how to get the link to reset their
      // password (sent in email, perhaps?)
      // toast.success(
      //   'A link to reset your ${passwordLowerCase} was sent to ' + response.email
      // )
      navigate(routes.login())
    }
  }

  return (
    <>
      <Metadata title="Forgot ${passwordTitleCase}" />

      <main>
        <Toaster toastOptions={{ className: 'rw-toast', duration: 6000 }} />
        <Card className="mx-auto mt-14 max-w-sm">
          <CardHeader>
            <CardTitle className="text-2xl">Forgot ${passwordTitleCase}</CardTitle>
            <CardDescription>
              Enter your ${usernameTitleCase} below and click Submit
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Form onSubmit={onSubmit}>
              <div className="grid gap-4">
                <div className="grid gap-2">
                  <Label
                    name="${usernameCamelCase}"
                    className="text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                    errorClassName="text-sm text-red-800 font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70"
                  >
                    ${usernameTitleCase}
                  </Label>
                  <TextField
                    name="${usernameCamelCase}"
                    className="flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium file:text-foreground placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
                    errorClassName="flex text-red-800 h-10 w-full rounded-md border border-red-800 border-input bg-background px-3 py-2 text-sm ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium file:text-foreground placeholder:text-muted-foreground focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50"
                    ref={${usernameCamelCase}Ref}
                    validation={{
                      required: {
                        value: true,
                        message: '${usernameTitleCase} is required',
                      },
                    }}
                  />

                  <FieldError name="${usernameCamelCase}" className="rw-field-error" />
                </div>

                <Button type="submit" className="w-full">
                  Submit
                </Button>
              </div>
            </Form>
          </CardContent>
        </Card>
      </main>
    </>
  )
}

export default ForgotPasswordPage
